pragma solidity >=0.4.22 <0.9.0;

contract Vote{

    uint public candiate1Vote;
    uint public candiate2Vote;
    mapping(address=>bool) voteCasted;
    constructor() public {
        candiate1Vote=0;
        candiate2Vote=0;
    }

    function castVote(uint candiate) public {
        require(!voteCasted[msg.sender] &&( candiate ==1 || candiate==2),"Vote has Already Been Casted");
        if(candiate==1)
        {
            candiate1Vote++;
            voteCasted[msg.sender]=true;
        }else{
            candiate2Vote++;
            voteCasted[msg.sender]=true;
        }

    }
}
